import React, { useState } from 'react';
import { Form, Button, Spinner } from 'react-bootstrap';
import axios from 'axios';

function SearchBar({ setResults, addRecentSearch, theme }) {
  const [query, setQuery] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!query) return;
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:5000/search?q=${query}`);
      setResults(response.data);
      addRecentSearch(query);
    } catch (error) {
      console.error('Error fetching results:', error);
      setResults([{ title: 'Error', url: '#', snippet: 'Something went wrong!' }]);
    }
    setLoading(false);
  };

  return (
    <Form onSubmit={handleSearch} className="d-flex justify-content-center mb-4">
      <div className="input-group w-75" style={{ maxWidth: '600px' }}>
        <Form.Control
          type="text"
          placeholder="Search..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          className="shadow-sm"
          style={{
            borderRadius: '25px 0 0 25px',
            borderRight: 'none',
            background: theme === 'dark' ? '#2e2e2e' : '#fff',
            color: theme === 'dark' ? '#fff' : '#000',
            border: theme === 'dark' ? '1px solid #555' : '1px solid #dfe1e5',
          }}
        />
        <Button
          variant="primary"
          type="submit"
          className="shadow-sm"
          style={{
            borderRadius: '0 25px 25px 0',
            borderLeft: 'none',
            background: '#1a0dab',
            border: 'none',
            padding: '10px 20px',
          }}
          disabled={loading}
        >
          {loading ? <Spinner animation="border" size="sm" /> : 'Search'}
        </Button>
      </div>
    </Form>
  );
}

export default SearchBar;
