import React, { useState, useEffect } from 'react';
import { Container, Button, Row, Col, Modal, Form } from 'react-bootstrap';
import SearchBar from './components/SearchBar';
import ResultCard from './components/ResultCard';

function App() {
  const [results, setResults] = useState([]);
  const [theme, setTheme] = useState('dark');
  const [recentSearches, setRecentSearches] = useState([]);
  const [showSettings, setShowSettings] = useState(false);
  const [backgroundImage, setBackgroundImage] = useState(
    theme === 'dark'
      ? 'https://images.unsplash.com/photo-1501139083538-0139583c060f?ixlib=rb-4.0.3&auto=format&fit=crop&w=1350&q=80'
      : 'https://images.unsplash.com/photo-1472214103451-9374bd1c798e?ixlib=rb-4.0.3&auto=format&fit=crop&w=1350&q=80'
  );

  useEffect(() => {
    document.body.className = theme;
    document.body.style.backgroundImage = `url(${backgroundImage})`;
  }, [theme, backgroundImage]);

  const handleThemeToggle = () => {
    const newTheme = theme === 'dark' ? 'light' : 'dark';
    setTheme(newTheme);
    setBackgroundImage(
      newTheme === 'dark'
        ? 'https://images.unsplash.com/photo-1501139083538-0139583c060f?ixlib=rb-4.0.3&auto=format&fit=crop&w=1350&q=80'
        : 'https://images.unsplash.com/photo-1472214103451-9374bd1c798e?ixlib=rb-4.0.3&auto=format&fit=crop&w=1350&q=80'
    );
  };

  const addRecentSearch = (query) => {
    if (!recentSearches.includes(query)) {
      const updatedSearches = [query, ...recentSearches.slice(0, 4)];
      setRecentSearches(updatedSearches);
    }
  };

  const handleBackgroundChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        setBackgroundImage(event.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className={`app-wrapper ${theme}`}>
      <Container className="py-4">
        <div className="d-flex justify-content-end mb-3">
          <Button
            variant={theme === 'dark' ? 'outline-light' : 'outline-dark'}
            className="me-2 animate__animated animate__flipInX"
            style={{
              borderRadius: '20px',
              padding: '8px 20px',
              fontWeight: 'bold',
              transition: 'all 0.3s',
              boxShadow: theme === 'dark' ? '0 0 10px rgba(255,255,255,0.2)' : '0 0 10px rgba(0,0,0,0.1)',
            }}
            onClick={handleThemeToggle}
          >
            {theme === 'dark' ? 'Light Mode' : 'Dark Mode'}
          </Button>
          <Button
            variant={theme === 'dark' ? 'outline-light' : 'outline-dark'}
            className="animate__animated animate__flipInX"
            style={{
              borderRadius: '20px',
              padding: '8px 20px',
              fontWeight: 'bold',
              transition: 'all 0.3s',
              boxShadow: theme === 'dark' ? '0 0 10px rgba(255,255,255,0.2)' : '0 0 10px rgba(0,0,0,0.1)',
            }}
            onClick={() => setShowSettings(true)}
          >
            Settings
          </Button>
        </div>
        <h1 className="text-center mb-4" style={{ fontFamily: 'Roboto', color: theme === 'dark' ? '#fff' : '#1a0dab' }}>
          Toggle AI
        </h1>
        <SearchBar setResults={setResults} addRecentSearch={addRecentSearch} theme={theme} />
        {recentSearches.length > 0 && (
          <Row className="mb-4">
            {recentSearches.map((search, index) => (
              <Col key={index} xs={6} md={2} className="mb-2">
                <a href="#" className="text-decoration-none" onClick={() => { setResults(mockResults(search)); addRecentSearch(search); }}>
                  <div className="p-2 bg-secondary bg-opacity-10 rounded text-center" style={{ color: theme === 'dark' ? '#fff' : '#000' }}>
                    <span role="img" aria-label="logo">🔍</span> {search}
                  </div>
                </a>
              </Col>
            ))}
          </Row>
        )}
        <div className="results mt-4">
          {results.length > 0 ? (
            results.map((result, index) => (
              <ResultCard key={index} result={result} index={index} theme={theme} />
            ))
          ) : (
            <p className="text-muted text-center" style={{ color: theme === 'dark' ? '#ccc' : '#666' }}>
              Enter a query to see results.
            </p>
          )}
        </div>
      </Container>

      {/* Settings Modal */}
      <Modal show={showSettings} onHide={() => setShowSettings(false)} centered>
        <Modal.Header closeButton style={{ background: theme === 'dark' ? '#2e2e2e' : '#fff', color: theme === 'dark' ? '#fff' : '#000' }}>
          <Modal.Title>Settings</Modal.Title>
        </Modal.Header>
        <Modal.Body style={{ background: theme === 'dark' ? '#2e2e2e' : '#fff', color: theme === 'dark' ? '#fff' : '#000' }}>
          <Form.Group controlId="backgroundImage">
            <Form.Label>Change Background Image</Form.Label>
            <Form.Control type="file" accept="image/*" onChange={handleBackgroundChange} />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer style={{ background: theme === 'dark' ? '#2e2e2e' : '#fff' }}>
          <Button variant="secondary" onClick={() => setShowSettings(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

// Mock function for results (remove when backend is fully integrated)
const mockResults = (query) => [
  { title: `${query} - Wikipedia`, url: `https://en.wikipedia.org/wiki/${query.replace(/\s+/g, '_')}`, snippet: `Wikipedia article about ${query}.` },
  { title: `${query} - Official Site`, url: `https://www.${query.toLowerCase()}.com`, snippet: `Official website for ${query}.` },
  { title: `News about ${query} - BBC`, url: `https://www.bbc.com/news/search?q=${query}`, snippet: `Latest news about ${query} from BBC.` },
];

export default App;
