import React from 'react';
import { Nav } from 'react-bootstrap';

function Tab({ tab, isActive, onClick, onClose }) {
  return (
    <Nav.Item>
      <Nav.Link
        eventKey={tab.id}
        onClick={onClick}
        style={{
          background: isActive ? '#1a0dab' : '#444',
          color: '#fff',
          borderRadius: '10px 10px 0 0',
          marginRight: '5px',
          padding: '5px 15px',
          position: 'relative',
        }}
      >
        {tab.title}
        <span
          onClick={(e) => {
            e.stopPropagation();
            onClose();
          }}
          style={{ marginLeft: '10px', cursor: 'pointer' }}
        >
          âœ•
        </span>
      </Nav.Link>
    </Nav.Item>
  );
}

export default Tab;
