import React, { useState } from 'react';
import { Form, Button, Spinner } from 'react-bootstrap';
import axios from 'axios';

function SearchBar({ setResults }) {
  const [query, setQuery] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSearch = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:5000/search?q=${query}`);
      setResults(response.data);
    } catch (error) {
      console.error('Error fetching results:', error);
      setResults([{ title: 'Error', url: '#', snippet: 'Something went wrong!' }]);
    }
    setLoading(false);
  };

  return (
    <Form onSubmit={handleSearch} className="mb-4 d-flex justify-content-center">
      <Form.Control
        type="text"
        placeholder="Search..."
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        className="w-50 shadow-sm animate__animated animate__pulse animate__infinite"
        style={{ borderRadius: '25px', border: '1px solid #dfe1e5', padding: '10px' }} /* Google-style */
      />
      <Button
        variant="outline-primary"
        type="submit"
        className="ms-2 animate__animated animate__bounceIn"
        disabled={loading}
        style={{ borderRadius: '25px', padding: '10px 20px' }}
      >
        {loading ? <Spinner animation="border" size="sm" /> : 'Search'}
      </Button>
    </Form>
  );
}

export default SearchBar;
