import React, { useState, useEffect } from 'react';
import { Container, Button, Row, Col, Modal, Form, Nav } from 'react-bootstrap';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import SearchBar from './components/SearchBar';
import SearchPage from './components/SearchPage';
import Tab from './components/Tab';

function App() {
  const [tabs, setTabs] = useState([{ id: 1, title: 'New Tab', path: '/' }]);
  const [activeTab, setActiveTab] = useState(1);
  const [theme, setTheme] = useState(localStorage.getItem('theme') || 'dark');
  const [backgroundImage, setBackgroundImage] = useState(
    localStorage.getItem('backgroundImage') ||
    (theme === 'dark'
      ? 'https://images.unsplash.com/photo-1501139083538-0139583c060f?ixlib=rb-4.0.3&auto=format&fit=crop&w=1350&q=80'
      : 'https://images.unsplash.com/photo-1472214103451-9374bd1c798e?ixlib=rb-4.0.3&auto=format&fit=crop&w=1350&q=80')
  );
  const [recentSearches, setRecentSearches] = useState(JSON.parse(localStorage.getItem('recentSearches')) || []);
  const [searchHistory, setSearchHistory] = useState(JSON.parse(localStorage.getItem('searchHistory')) || []);
  const [showSettings, setShowSettings] = useState(false);

  useEffect(() => {
    document.body.className = theme;
    document.body.style.backgroundImage = `url(${backgroundImage})`;
    localStorage.setItem('theme', theme);
    localStorage.setItem('backgroundImage', backgroundImage);
    localStorage.setItem('recentSearches', JSON.stringify(recentSearches));
    localStorage.setItem('searchHistory', JSON.stringify(searchHistory));
  }, [theme, backgroundImage, recentSearches, searchHistory]);

  const handleThemeToggle = () => {
    const newTheme = theme === 'dark' ? 'light' : 'dark';
    setTheme(newTheme);
    setBackgroundImage(
      newTheme === 'dark'
        ? 'https://images.unsplash.com/photo-1501139083538-0139583c060f?ixlib=rb-4.0.3&auto=format&fit=crop&w=1350&q=80'
        : 'https://images.unsplash.com/photo-1472214103451-9374bd1c798e?ixlib=rb-4.0.3&auto=format&fit=crop&w=1350&q=80'
    );
  };

  const addTab = () => {
    const newTab = { id: tabs.length + 1, title: 'New Tab', path: '/' };
    setTabs([...tabs, newTab]);
    setActiveTab(newTab.id);
  };

  const closeTab = (id) => {
    const updatedTabs = tabs.filter((tab) => tab.id !== id);
    if (updatedTabs.length === 0) {
      setTabs([{ id: 1, title: 'New Tab', path: '/' }]);
      setActiveTab(1);
    } else {
      setTabs(updatedTabs);
      if (activeTab === id) {
        setActiveTab(updatedTabs[0].id);
      }
    }
  };

  const closeAllTabs = () => {
    setTabs([{ id: 1, title: 'New Tab', path: '/' }]);
    setActiveTab(1);
  };

  const updateTab = (id, title, path) => {
    const updatedTabs = tabs.map((tab) =>
      tab.id === id ? { ...tab, title, path } : tab
    );
    setTabs(updatedTabs);
  };

  const handleBackgroundChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        setBackgroundImage(event.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const clearHistory = () => {
    setSearchHistory([]);
    setRecentSearches([]);
  };

  const deleteHistoryItem = (index) => {
    const updatedHistory = searchHistory.filter((_, i) => i !== index);
    setSearchHistory(updatedHistory);
    const updatedSearches = recentSearches.filter((search) => !updatedHistory.some((item) => item.query === search));
    setRecentSearches(updatedSearches);
  };

  return (
    <Router>
      <div className={`app-wrapper ${theme}`}>
        <Container fluid className="py-2">
          <Nav className="mb-3">
            {tabs.map((tab) => (
              <Tab
                key={tab.id}
                tab={tab}
                isActive={activeTab === tab.id}
                onClick={() => setActiveTab(tab.id)}
                onClose={() => closeTab(tab.id)}
              />
            ))}
            <Button
              variant="outline-secondary"
              className="ms-2"
              style={{ borderRadius: '20px', padding: '5px 10px' }}
              onClick={addTab}
            >
              +
            </Button>
            <Button
              variant="outline-danger"
              className="ms-2"
              style={{ borderRadius: '20px', padding: '5px 10px' }}
              onClick={closeAllTabs}
            >
              Close All Tabs
            </Button>
            <div className="ms-auto">
              <Button
                variant={theme === 'dark' ? 'outline-light' : 'outline-dark'}
                className="me-2 animate__animated animate__flipInX"
                style={{
                  borderRadius: '20px',
                  padding: '8px 20px',
                  fontWeight: 'bold',
                  transition: 'all 0.3s',
                  boxShadow: theme === 'dark' ? '0 0 10px rgba(255,255,255,0.5)' : '0 0 10px rgba(0,0,0,0.3)',
                  background: theme === 'dark' ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.05)',
                }}
                onClick={handleThemeToggle}
              >
                {theme === 'dark' ? 'Light Mode' : 'Dark Mode'}
              </Button>
              <Button
                variant={theme === 'dark' ? 'outline-light' : 'outline-dark'}
                className="animate__animated animate__flipInX"
                style={{
                  borderRadius: '20px',
                  padding: '8px 20px',
                  fontWeight: 'bold',
                  transition: 'all 0.3s',
                  boxShadow: theme === 'dark' ? '0 0 10px rgba(255,255,255,0.5)' : '0 0 10px rgba(0,0,0,0.3)',
                  background: theme === 'dark' ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.05)',
                }}
                onClick={() => setShowSettings(true)}
              >
                Settings
              </Button>
            </div>
          </Nav>
          <Routes>
            {tabs.map((tab) => (
              <Route
                key={tab.id}
                path={tab.path === '/' ? '/' : `/search/${tab.path}`}
                element={
                  tab.path === '/' ? (
                    <MainPage
                      addRecentSearch={(query) => {
                        setRecentSearches((prev) => !prev.includes(query) ? [query, ...prev.slice(0, 4)] : prev);
                        setSearchHistory((prev) => [
                          { query, timestamp: new Date().toLocaleString() },
                          ...prev,
                        ]);
                      }}
                      recentSearches={recentSearches}
                      theme={theme}
                      updateTab={(title, path) => updateTab(tab.id, title, path)}
                    />
                  ) : (
                    <SearchPage query={tab.path} theme={theme} />
                  )
                }
              />
            ))}
          </Routes>
        </Container>

        {/* Settings Modal */}
        <Modal show={showSettings} onHide={() => setShowSettings(false)} centered>
          <Modal.Header closeButton style={{ background: theme === 'dark' ? '#2e2e2e' : '#fff', color: theme === 'dark' ? '#fff' : '#000' }}>
            <Modal.Title>Settings</Modal.Title>
          </Modal.Header>
          <Modal.Body style={{ background: theme === 'dark' ? '#2e2e2e' : '#fff', color: theme === 'dark' ? '#fff' : '#000' }}>
            <Form.Group controlId="backgroundImage" className="mb-3">
              <Form.Label>Change Background Image</Form.Label>
              <Form.Control type="file" accept="image/*" onChange={handleBackgroundChange} />
            </Form.Group>
            <h5>Search History</h5>
            {searchHistory.length > 0 ? (
              <ul className="list-group">
                {searchHistory.map((item, index) => (
                  <li key={index} className="list-group-item d-flex justify-content-between align-items-center" style={{ background: theme === 'dark' ? '#3e3e3e' : '#f8f9fa' }}>
                    <span>{item.query} - {item.timestamp}</span>
                    <Button variant="danger" size="sm" onClick={() => deleteHistoryItem(index)}>
                      Delete
                    </Button>
                  </li>
                ))}
              </ul>
            ) : (
              <p>No search history available.</p>
            )}
            <Button variant="danger" className="mt-3" onClick={clearHistory}>
              Clear All History
            </Button>
          </Modal.Body>
          <Modal.Footer style={{ background: theme === 'dark' ? '#2e2e2e' : '#fff' }}>
            <Button variant="secondary" onClick={() => setShowSettings(false)}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      </div>
    </Router>
  );
}

function MainPage({ addRecentSearch, recentSearches, theme, updateTab }) {
  const navigate = useNavigate();

  const handleSearch = (query) => {
    addRecentSearch(query);
    updateTab(`Search: ${query}`, query);
    navigate(`/search/${query}`);
  };

  return (
    <Container className="py-4 text-center">
      <h1 className="mb-4" style={{ fontFamily: 'Montserrat', color: theme === 'dark' ? '#00d4ff' : '#ff007a', textShadow: '0 0 10px rgba(0,212,255,0.5)' }}>
        Toggle AI
      </h1>
      <SearchBar onSearch={handleSearch} theme={theme} />
      {recentSearches.length > 0 && (
        <Row className="mt-4">
          {recentSearches.map((search, index) => (
            <Col key={index} xs={6} md={2} className="mb-2">
              <Button
                variant={theme === 'dark' ? 'outline-light' : 'outline-dark'}
                className="w-100"
                onClick={() => handleSearch(search)}
              >
                <span role="img" aria-label="logo">🔍</span> {search}
              </Button>
            </Col>
          ))}
        </Row>
      )}
    </Container>
  );
}

export default App;
