import React, { useState, useEffect } from 'react';
import { Container } from 'react-bootstrap';
import axios from 'axios';
import ResultCard from './ResultCard';

function SearchPage({ query, theme }) {
  const [results, setResults] = useState([]);

  useEffect(() => {
    const fetchResults = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/search?q=${query}`);
        setResults(response.data);
      } catch (error) {
        console.error('Error fetching results:', error);
        setResults([{ title: 'Error', url: '#', snippet: 'Something went wrong!' }]);
      }
    };
    fetchResults();
  }, [query]);

  return (
    <div style={{ background: '#000', minHeight: '100vh' }}>
      <Container className="py-4">
        <h2 style={{ color: '#fff' }}>Results for "{query}"</h2>
        <div className="results mt-4">
          {results.map((result, index) => (
            <ResultCard key={index} result={result} index={index} theme={theme} />
          ))}
        </div>
      </Container>
    </div>
  );
}

export default SearchPage;
